templates.template:
public static final String CMD = "";
	private Category  logger =  Category.getInstance(Bms_stwarn_createadjusttmp_dbprocess.class);
	@Override
	public int process(Userruninfo userinfo, ClientRequest req,
			ServerResponse resp) throws Exception {
		String cmd = req.getCommand();
		if (!CMD.equals(cmd)) {
			throw new Exception("服务器命令错误！");
		}
		Connection con = null;
		
		try{
			con = super.getConnection();

			con.commit();
		}catch(Exception e){
			super.rollback(con);
			logger.error("error", e);
			throw e;
		}finally{
			super.close(con);
		}
		return super.process(userinfo, req, resp);
	}
